import React, { useState } from "react";

const getTodayString = () => {
  const [month, day, year] = new Date()
    .toLocaleDateString("en-US")
    .split("/");
  return `${year}-${month.padStart(2, "0")}-${day.padStart(2, "0")}`;
};

export const AppointmentForm = () => {
  const [contacts, setContacts] = useState('');
  const [title, setTitle] = useState('');
  const [contact, setContact] = useState('');
  const [date, setDate] = useState(getTodayString()); // Set default value to today
  const [time, setTime] = useState('');

  const handleContactsChange = (event) => {
    setContacts(event.target.value);
  };

  const handleTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const handleContactChange = (event) => {
    setContact(event.target.value);
  };

  const handleDateChange = (event) => {
    setDate(event.target.value);
  };

  const handleTimeChange = (event) => {
    setTime(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    
    // Do something with the appointment data
    console.log({ contacts, title, contact, date, time });

    // Clear form fields
    setContacts('');
    setTitle('');
    setContact('');
    setDate(getTodayString()); // Reset date to today
    setTime('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Contacts:</label>
        <input type="text" value={contacts} onChange={handleContactsChange} />
      </div>
      <div>
        <label>Title:</label>
        <input type="text" value={title} onChange={handleTitleChange} />
      </div>
      <div>
        <label>Contact:</label>
        <input type="text" value={contact} onChange={handleContactChange} />
      </div>
      <div>
        <label>Date:</label>
        <input type="date" value={date} onChange={handleDateChange} />
      </div>
      <div>
        <label>Time:</label>
        <input type="time" value={time} onChange={handleTimeChange} />
      </div>
      <button type="submit">Add Appointment</button>
    </form>
  );
};
